tables.table_schema:
CREATE TABLE drug_manufacturing_processes (
    process_id INT PRIMARY KEY,
    process_name TEXT,
    description TEXT
);

CREATE TABLE fda_regulations (
    regulation_id INT PRIMARY KEY,
    regulation_name TEXT,
    regulation_description TEXT
);

CREATE TABLE manufacturing_sites (
    site_id INT PRIMARY KEY,
    site_name TEXT NOT NULL,
    location TEXT
);

CREATE TABLE manufacturing_processes (
    process_id INT PRIMARY KEY,
    process_name TEXT NOT NULL,
    description TEXT
);

CREATE TABLE manufacturing_sites (
    site_id INT PRIMARY KEY,
    site_name VARCHAR(255) NOT NULL,
    site_location VARCHAR(255) NOT NULL
);

CREATE TABLE manufacturers (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255) NOT NULL,
    contact_person_name VARCHAR(255) NOT NULL,
    contact_person_email VARCHAR(255) NOT NULL
);

CREATE TABLE ingredients (
    ingredient_id INT PRIMARY KEY,
    ingredient_name VARCHAR(255) NOT NULL,
    supplier_name VARCHAR(255) NOT NULL,
    supplier_contact VARCHAR(255) NOT NULL
);

CREATE TABLE manufacturing_facility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(100) NOT NULL,
    location VARCHAR(255),
    capacity INT
);
