tables.table_schema:
CREATE TABLE manufacturing_facilities (
    facility_id UUID PRIMARY KEY,
    facility_name STRING NOT NULL,
    location STRING,
    inspection_date DATE
);

CREATE TABLE inspection_reports (
    report_id UUID PRIMARY KEY,
    facility_id UUID REFERENCES manufacturing_facilities(facility_id),
    inspector_name STRING NOT NULL,
    report_date DATE,
    compliance_status BOOLEAN
);

CREATE TABLE PharmaceuticalInspectors (
	hospital_id INT PRIMARY KEY,
	inspector_name VARCHAR(50) NOT NULL,
	hospital_name VARCHAR(100) NOT NULL
);

CREATE TABLE ManufacturingFacilities (
	facility_id INT PRIMARY KEY,
	facility_name VARCHAR(100) NOT NULL,
	location VARCHAR(50) NOT NULL,
	inspection_status BOOLEAN
);
