CREATE TABLE medications (
    id UUID PRIMARY KEY,
    medication_name VARCHAR(255),
    temperature_celsius DECIMAL,
    storage_conditions JSON
);

CREATE TABLE medications (
    medication_id INT PRIMARY KEY,
    medication_name VARCHAR(255) NOT NULL,
    storage_temperature FLOAT NOT NULL,
    storage_conditions VARCHAR(255)
);

CREATE TABLE medications(
    medication_id INT PRIMARY KEY,
    medication_name VARCHAR(255) NOT NULL,
    temperature_storage VARCHAR(50),
    storage_condition VARCHAR(50)
);

CREATE TABLE storage_conditions(
    condition_id INT PRIMARY KEY,
    condition_name VARCHAR(50) NOT NULL
);

CREATE TABLE medications ( 
 id SERIAL PRIMARY KEY, 
 medication_name VARCHAR(255) NOT NULL, 
 temperature_recorded DECIMAL, 
 storage_conditions VARCHAR(255) 
);

CREATE TABLE temperature_logs ( 
 log_id SERIAL PRIMARY KEY, 
 medication_id INTEGER REFERENCES medications(id), 
 temperature DECIMAL, 
 timestamp TIMESTAMP 
);

CREATE TABLE storage_conditions ( 
 condition_id SERIAL PRIMARY KEY, 
 condition_name VARCHAR(255) NOT NULL 
);

CREATE TABLE regulatory_guidelines ( 
 guideline_id SERIAL PRIMARY KEY, 
 guideline_description TEXT 
);

CREATE TABLE compliance_records ( 
 record_id SERIAL PRIMARY KEY, 
 medication_id INTEGER REFERENCES medications(id), 
 guideline_id INTEGER REFERENCES regulatory_guidelines(guideline_id), 
 compliance_status BOOLEAN 
);
