tables.table_schema:
CREATE TABLE pharmaceutical_companies (
  company_id INT PRIMARY KEY,
  company_name VARCHAR(255) NOT NULL,
  company_address VARCHAR(255) NOT NULL
);

CREATE TABLE pharmaceutical_companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    headquarters_address TEXT,
    contact_number VARCHAR(15)
);

CREATE TABLE PharmaceuticalCompanies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(100) NOT NULL
);

CREATE TABLE Drugs (
    drug_id INT PRIMARY KEY,
    drug_name VARCHAR(100) NOT NULL,
    company_id INT,
    FOREIGN KEY (company_id) REFERENCES PharmaceuticalCompanies(company_id)
);

CREATE TABLE RegulatoryOversight (
    oversight_id INT PRIMARY KEY,
    company_id INT,
    regulation_details TEXT,
    FOREIGN KEY (company_id) REFERENCES PharmaceuticalCompanies(company_id)
);
