tables.table_schema:
CREATE TABLE drug_compounds (
  compound_id INT PRIMARY KEY,
  compound_name TEXT,
  molecular_structure TEXT,
  molecular_weight FLOAT
);

CREATE TABLE compound_structures (
    compound_id SERIAL PRIMARY KEY,
    compound_name VARCHAR(255) NOT NULL,
    structure TEXT
);

CREATE TABLE drug_compound_structures (
    compound_id INT PRIMARY KEY,
    compound_name VARCHAR(255),
    molecular_formula VARCHAR(50),
    molecular_weight DECIMAL(10,2)
);

CREATE TABLE research_processes (
    process_id INT PRIMARY KEY,
    process_name VARCHAR(100),
    start_date DATE,
    end_date DATE
);
