tables.table_schema:
CREATE TABLE access_control (
    role_id UUID PRIMARY KEY,
    role_name VARCHAR(50),
    permissions JSONB,
    created_at TIMESTAMP
);

CREATE TABLE Researchers (
    researcher_id INT PRIMARY KEY,
    researcher_name VARCHAR(50),
    research_area VARCHAR(100),
    encryption_key VARCHAR(50)
);

CREATE TABLE encryption_keys (
    key_id INT PRIMARY KEY,
    key_name VARCHAR(50),
    key_value VARCHAR(100)
);

CREATE TABLE sensitive_research_data (
  id SERIAL PRIMARY KEY,
  researcher_name TEXT NOT NULL,
  research_topic TEXT NOT NULL,
  data_encryption_key TEXT NOT NULL
);

CREATE TABLE research_permissions (
  id SERIAL PRIMARY KEY,
  researcher_id INT NOT NULL,
  access_level ENUM('read', 'write', 'admin') NOT NULL,
  access_key TEXT NOT NULL
);

CREATE TABLE research_logs (
  id SERIAL PRIMARY KEY,
  researcher_id INT NOT NULL,
  action_timestamp TIMESTAMP NOT NULL,
  action_description TEXT NOT NULL
);

CREATE TABLE research_access_audit (
  id SERIAL PRIMARY KEY,
  user_id INT NOT NULL,
  action_timestamp TIMESTAMP NOT NULL,
  action_description TEXT NOT NULL
);

CREATE TABLE research_access_control (
  id SERIAL PRIMARY KEY,
  role_name TEXT UNIQUE NOT NULL,
  role_description TEXT NOT NULL
);
