tables.table_schema:
CREATE TABLE social_media_data (
    id INT PRIMARY KEY,
    post_content TEXT,
    user_id INT,
    sentiment_analysis DECIMAL
);

CREATE TABLE consumer_preferences (
    user_id INT PRIMARY KEY,
    product_id INT,
    sentiment_score DECIMAL
);

CREATE TABLE SocialMediaData (
    id INT PRIMARY KEY,
    post_text TEXT,
    sentiment_score FLOAT
);

CREATE TABLE ConsumerSentiment (
    id INT PRIMARY KEY,
    user_id INT,
    sentiment_category TEXT,
    sentiment_score FLOAT
);

CREATE TABLE ConsumerPreferences (
    id INT PRIMARY KEY,
    user_id INT,
    product_id INT,
    preference_score FLOAT
);

CREATE TABLE social_media_sentiment_analysis (
    id INT PRIMARY KEY,
    username VARCHAR(50),
    post TEXT,
    sentiment_score FLOAT
);

CREATE TABLE user_demographics (
    user_id INT PRIMARY KEY,
    gender VARCHAR(10),
    age INT,
    location VARCHAR(100)
);

CREATE TABLE product_reviews (
    review_id INT PRIMARY KEY,
    product_id INT,
    review_text TEXT,
    rating FLOAT
);

CREATE TABLE marketing_campaigns (
    campaign_id INT PRIMARY KEY,
    campaign_name VARCHAR(100),
    start_date DATE,
    end_date DATE
);
