tables.table_schema:
CREATE TABLE visits (
  visit_id INT PRIMARY KEY,
  rep_id INT,
  customer_id INT,
  visit_date DATE
);

CREATE TABLE feedback (
  feedback_id INT PRIMARY KEY,
  visit_id INT,
  feedback_text TEXT,
  feedback_date DATE
);

CREATE TABLE SalesRepresentative (
    id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE CustomerVisit (
    visit_id UUID PRIMARY KEY,
    representative_id UUID REFERENCES SalesRepresentative(id),
    visit_date DATE NOT NULL,
    feedback TEXT
);

CREATE TABLE SalesPerformance (
    performance_id UUID PRIMARY KEY,
    representative_id UUID REFERENCES SalesRepresentative(id),
    sale_date DATE NOT NULL,
    amount DECIMAL(10, 2) NOT NULL
);

CREATE TABLE sales_representatives (
    representative_id UUID PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    email VARCHAR(100) UNIQUE,
    phone_number VARCHAR(20)
);

CREATE TABLE sales_performance (
    performance_id UUID PRIMARY KEY,
    representative_id UUID REFERENCES sales_representatives(representative_id),
    sales_amount DECIMAL(10, 2),
    sales_date DATE
);

CREATE TABLE strategic_planning (
    planning_id UUID PRIMARY KEY,
    representative_id UUID REFERENCES sales_representatives(representative_id),
    plan_description TEXT,
    plan_date DATE
);

CREATE TABLE reps (
    rep_id INT PRIMARY KEY,
    rep_name TEXT,
    rep_email TEXT
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    customer_email TEXT
);
