tables.table_schema:
CREATE TABLE regulatory_compliance_documents (
  document_id UUID PRIMARY KEY,
  product_name TEXT NOT NULL,
  document_type TEXT NOT NULL,
  document_owner TEXT NOT NULL
);

CREATE TABLE document_versions (
  version_id INT PRIMARY KEY,
  document_id UUID NOT NULL,
  version_date DATE NOT NULL,
  version_notes TEXT
);

CREATE TABLE regulatory_agencies (
  agency_id SERIAL PRIMARY KEY,
  agency_name TEXT NOT NULL,
  country TEXT NOT NULL
);

CREATE TABLE regulatory_guidelines (
  doc_id INT PRIMARY KEY,
  title TEXT NOT NULL,
  category TEXT
);

CREATE TABLE product_documents (
  doc_id INT PRIMARY KEY,
  product_id INT NOT NULL,
  doc_url VARCHAR(255) UNIQUE,
  version INT
);

CREATE TABLE compliance_tasks (
  task_id INT PRIMARY KEY,
  task_description TEXT,
  completion_date DATE
);

CREATE TABLE product_regulations (
  reg_id INT PRIMARY KEY,
  product_id INT NOT NULL,
  reg_description TEXT
);

CREATE TABLE compliance_reports (
  report_id INT PRIMARY KEY,
  report_date DATE,
  reviewer_id INT
);
