tables.table_schema:
CREATE TABLE storage_temperature (
  id INT PRIMARY KEY,
  drug_name VARCHAR(50),
  temperature FLOAT
);

CREATE TABLE temperature_logs (
  log_id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  temperature_record FLOAT
);

CREATE TABLE TemperatureSensor (
	sensor_id INT PRIMARY KEY,
	location VARCHAR,
	sensor_type VARCHAR,
	status VARCHAR
);

CREATE TABLE Drug (
	drug_id INT PRIMARY KEY,
	drug_name VARCHAR,
	storage_condition VARCHAR
);
