tables.table_schema:
CREATE TABLE procedures (
    procedure_id INT PRIMARY KEY,
    procedure_name VARCHAR(255) NOT NULL,
    created_date TIMESTAMP NOT NULL
);

CREATE TABLE steps (
    step_id INT PRIMARY KEY,
    step_description TEXT NOT NULL,
    procedure_id INT,
    FOREIGN KEY (procedure_id) REFERENCES procedures(procedure_id)
);

CREATE TABLE manufacturing_processes (
  process_id INT PRIMARY KEY,
  process_description TEXT
);

CREATE TABLE sop_templates (
  template_id INT PRIMARY KEY,
  template_name TEXT,
  template_content TEXT
);

CREATE TABLE sop_updates (
  update_id INT PRIMARY KEY,
  template_id INT,
  update_description TEXT,
  update_date TIMESTAMP
);

CREATE TABLE standard_operating_procedures (
    id INT AUTO_INCREMENT PRIMARY KEY,
    title VARCHAR(255) NOT NULL,
    description TEXT,
    last_updated TIMESTAMP
);

CREATE TABLE manufacturing_processes (
    id INT AUTO_INCREMENT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    description TEXT,
    created_at TIMESTAMP
);

CREATE TABLE process_steps (
    id INT AUTO_INCREMENT PRIMARY KEY,
    step_name VARCHAR(255) NOT NULL,
    duration_minutes INT,
    description TEXT
);

CREATE TABLE step_history (
    id INT AUTO_INCREMENT PRIMARY KEY,
    process_step_id INT,
    completed_at TIMESTAMP,
    CONSTRAINT fk_process_step_id FOREIGN KEY (process_step_id) REFERENCES process_steps(id)
);
