tables.table_schema:
CREATE TABLE packaging_labels (
  label_id INT PRIMARY KEY,
  label_text TEXT
);

CREATE TABLE packaging_barcode (
  barcode_id INT PRIMARY KEY,
  barcode_value TEXT
);

CREATE TABLE Labels (
    label_id INT PRIMARY KEY,
    label_name VARCHAR(255),
    label_description TEXT
);

CREATE TABLE Barcodes (
    barcode_id INT PRIMARY KEY,
    barcode_value VARCHAR(50),
    product_id INT,
    FOREIGN KEY (product_id) REFERENCES Labels(label_id)
);

CREATE TABLE SerialNumbers (
    serial_number_id INT PRIMARY KEY,
    serial_number_value VARCHAR(50),
    product_id INT,
    FOREIGN KEY (product_id) REFERENCES Labels(label_id)
);

CREATE TABLE labels (
  label_id INT PRIMARY KEY,
  label_name VARCHAR(50) NOT NULL,
  creation_date DATE
);

CREATE TABLE barcodes (
  barcode_id INT PRIMARY KEY,
  barcode_value VARCHAR(20) UNIQUE NOT NULL,
  product_id INT REFERENCES products(product_id)
);

CREATE TABLE serial_numbers (
  serial_number_id INT PRIMARY KEY,
  serial_number_value VARCHAR(30) UNIQUE NOT NULL,
  product_id INT REFERENCES products(product_id)
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(50) NOT NULL,
  category VARCHAR(50) NOT NULL
);

CREATE TABLE labels (
    id INT PRIMARY KEY,
    label_name VARCHAR(255),
    label_type VARCHAR(50),
    manufacturer_id INT
);

CREATE TABLE barcodes (
    id INT PRIMARY KEY,
    barcode_value VARCHAR(50),
    product_id INT,
    created_date TIMESTAMP
);

CREATE TABLE serial_numbers (
    id INT PRIMARY KEY,
    serial_number_value VARCHAR(50),
    product_id INT,
    manufacturing_date DATE
);

CREATE TABLE products (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    manufacturer_id INT
);

CREATE TABLE manufacturers (
    id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255),
    headquarters_location VARCHAR(255)
);
