tables.table_schema:
CREATE TABLE defect_patterns (
    pattern_id INT PRIMARY KEY,
    pattern_description VARCHAR(255),
    relevant_defect_types VARCHAR(255),
    analysis_result TEXT
);

CREATE TABLE defects (
  defect_id SERIAL PRIMARY KEY,
  defect_type VARCHAR(50) NOT NULL,
  defect_description VARCHAR(255) NOT NULL,
  defect_date DATE
);

CREATE TABLE defects (
  defect_id INT PRIMARY KEY,
  defect_name VARCHAR(255),
  defect_type VARCHAR(100),
  defect_date DATE
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  product_type VARCHAR(100),
  manufacturing_date DATE
);

CREATE TABLE locations (
  location_id INT PRIMARY KEY,
  location_name VARCHAR(255),
  city VARCHAR(100),
  country VARCHAR(100)
);
