tables.table_schema:
CREATE TABLE Product (
    barcode_id VARCHAR PRIMARY KEY,
    product_name VARCHAR,
    manufacture_date TIMESTAMP,
    expiry_date TIMESTAMP
);

CREATE TABLE pharmaceutical_products (
   product_id INT PRIMARY KEY,
   product_name VARCHAR(255),
   barcode VARCHAR(50) UNIQUE
);

CREATE TABLE barcode_scanners (
    id INT PRIMARY KEY,
    scanner_name VARCHAR(50) NOT NULL,
    manufacturer VARCHAR(50) NOT NULL
);

CREATE TABLE pharmaceutical_products (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(100) NOT NULL,
    manufacturing_date DATE NOT NULL,
    expiry_date DATE NOT NULL
);

CREATE TABLE packaging_units (
    unit_id INT PRIMARY KEY,
    product_id INT NOT NULL,
    quantity INT NOT NULL
);

CREATE TABLE scanned_data (
    id INT PRIMARY KEY,
    scanner_id INT NOT NULL,
    unit_id INT NOT NULL,
    scan_time TIMESTAMP NOT NULL
);

CREATE TABLE ProductInformation (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    manufacturer VARCHAR(255),
    barcode VARCHAR(50)
);

CREATE TABLE AuthenticationLog (
    log_id INT PRIMARY KEY,
    scanned_barcode VARCHAR(50),
    verification_result VARCHAR(50),
    verification_datetime TIMESTAMP
);

CREATE TABLE UserAccounts (
    user_id INT PRIMARY KEY,
    username VARCHAR(50),
    email VARCHAR(100)
);
