tables.table_schema:
CREATE TABLE temperature_conditions (
 sensor_id INT PRIMARY KEY,
 timestamp TIMESTAMP,
 temperature FLOAT
);

CREATE TABLE humidity_conditions (
 sensor_id INT PRIMARY KEY,
 timestamp TIMESTAMP,
 humidity FLOAT
);

CREATE TABLE temperature_sensors (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(255),
    temperature FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE humidity_sensors (
    sensor_id INT PRIMARY KEY,
    location VARCHAR(255),
    humidity FLOAT,
    timestamp TIMESTAMP
);

CREATE TABLE temperature_conditions (
  location_id INT PRIMARY KEY,
  temperature FLOAT,
  date_time TIMESTAMP
);

CREATE TABLE humidity_conditions (
  location_id INT PRIMARY KEY,
  humidity FLOAT,
  date_time TIMESTAMP
);

CREATE TABLE storage_facilities (
  location_id INT PRIMARY KEY,
  facility_name VARCHAR(50),
  facility_manager VARCHAR(50)
);

CREATE TABLE storage_facility (
    facility_id INT PRIMARY KEY,
    facility_name VARCHAR(50) NOT NULL,
    temperature_sensor_id INT NOT NULL,
    humidity_sensor_id INT NOT NULL
);

CREATE TABLE temperature_sensor (
    sensor_id INT PRIMARY KEY,
    temperature_value DECIMAL(5,2) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE humidity_sensor (
    sensor_id INT PRIMARY KEY,
    humidity_value DECIMAL(5,2) NOT NULL,
    timestamp TIMESTAMP NOT NULL
);

CREATE TABLE packaging (
    package_id INT PRIMARY KEY,
    product_name VARCHAR(50) NOT NULL,
    expiration_date DATE NOT NULL,
    storage_facility_id INT NOT NULL
);

CREATE TABLE sensor_location (
    location_id INT PRIMARY KEY,
    sensor_id INT NOT NULL,
    facility_id INT NOT NULL
);
