tables.table_schema:
CREATE TABLE packaging_specifications (
  id INT PRIMARY KEY,
  product_name VARCHAR(255),
  weight DECIMAL,
  dimensions VARCHAR(50)
);

CREATE TABLE safety_standards (
  id INT PRIMARY KEY,
  standard_name VARCHAR(255),
  description TEXT,
  certification_date DATE
);

CREATE TABLE safety_standards (
    id INT PRIMARY KEY,
    standard_name VARCHAR(255) NOT NULL,
    description TEXT,
    last_update DATE
);

CREATE TABLE safety_standards (
  id INT PRIMARY KEY,
  standard_name VARCHAR(255),
  category VARCHAR(50),
  description TEXT
);

CREATE TABLE compliance_reports (
  id INT PRIMARY KEY,
  report_date DATE,
  submitted_by VARCHAR(100),
  compliance_status BOOLEAN
);

CREATE TABLE packaging_reports (
  id INT PRIMARY KEY,
  report_id INT,
  packaging_spec_id INT,
  safety_standard_id INT
);

CREATE TABLE audit_log (
  id INT PRIMARY KEY,
  timestamp TIMESTAMP,
  event_description TEXT,
  user_id INT
);
