tables.table_schema:
CREATE TABLE manufacturers (
  manufacturer_id INT PRIMARY KEY,
  manufacturer_name VARCHAR(100) NOT NULL
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(100) NOT NULL,
  manufacturer_id INT,
  FOREIGN KEY (manufacturer_id) REFERENCES manufacturers(manufacturer_id)
);

CREATE TABLE Manufacturers (
 manufacturer_id INT PRIMARY KEY,
 manufacturer_name VARCHAR(255) NOT NULL
);

CREATE TABLE Wholesalers (
 wholesaler_id INT PRIMARY KEY,
 wholesaler_name VARCHAR(255) NOT NULL,
 manufacturer_id INT,
 FOREIGN KEY (manufacturer_id) REFERENCES Manufacturers(manufacturer_id)
);

CREATE TABLE Retailers (
 retailer_id INT PRIMARY KEY,
 retailer_name VARCHAR(255) NOT NULL,
 wholesaler_id INT,
 FOREIGN KEY (wholesaler_id) REFERENCES Wholesalers(wholesaler_id)
);

CREATE TABLE manufacturer (
    manufacturer_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255),
    manufacturer_address VARCHAR(255)
);

CREATE TABLE wholesaler (
    wholesaler_id INT PRIMARY KEY,
    wholesaler_name VARCHAR(255),
    wholesaler_location VARCHAR(255)
);

CREATE TABLE retailer (
    retailer_id INT PRIMARY KEY,
    retailer_name VARCHAR(255),
    retailer_location VARCHAR(255)
);

CREATE TABLE manufacturer (
    manufacturer_id UUID PRIMARY KEY,
    manufacturer_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE wholesaler (
    wholesaler_id UUID PRIMARY KEY,
