tables.table_schema:
CREATE TABLE regulatory_audits (
  audit_id INT PRIMARY KEY,
  audit_type VARCHAR(255) NOT NULL,
  audit_date DATE NOT NULL,
  inspector_name VARCHAR(255) NOT NULL
);

CREATE TABLE AuditDetails (
    audit_id INT,
    audit_question VARCHAR(100),
    audit_answer VARCHAR(100),
    FOREIGN KEY (audit_id) REFERENCES Audit(audit_id)
);

CREATE TABLE InspectionDetails (
    inspection_id INT,
    inspection_question VARCHAR(100),
    inspection_answer VARCHAR(100),
    FOREIGN KEY (inspection_id) REFERENCES Inspection(inspection_id)
);

CREATE TABLE audit_details (
    audit_id UUID PRIMARY KEY,
    audit_name VARCHAR(255) NOT NULL,
    audit_date TIMESTAMP NOT NULL,
    auditor_name VARCHAR(255) NOT NULL
);

CREATE TABLE audit_findings (
    finding_id UUID PRIMARY KEY,
    audit_id UUID REFERENCES audit_details(audit_id),
    finding_text TEXT NOT NULL
);

CREATE TABLE inspection_details (
    inspection_id UUID PRIMARY KEY,
    inspection_type VARCHAR(100) NOT NULL,
    inspection_date TIMESTAMP NOT NULL,
    inspector_name VARCHAR(255) NOT NULL
);

CREATE TABLE inspection_findings (
    finding_id UUID PRIMARY KEY,
    inspection_id UUID REFERENCES inspection_details(inspection_id),
    finding_text TEXT NOT NULL
);

CREATE TABLE compliance_stats (
    stat_id UUID PRIMARY KEY,
    audit_id UUID REFERENCES audit_details(audit_id),
    inspection_id UUID REFERENCES inspection_details(inspection_id),
    compliance_score INT
);
