tables.table_schema:
CREATE TABLE royalty_payments (
    payment_id SERIAL PRIMARY KEY,
    agreement_id INT REFERENCES licensing_agreements(agreement_id),
    payment_amount DECIMAL NOT NULL,
    payment_date DATE
);

CREATE TABLE patents (id SERIAL PRIMARY KEY,
    patent_number VARCHAR(255) NOT NULL,
    patent_description TEXT,
    expiry_date DATE
);

CREATE TABLE royalty_terms (id SERIAL PRIMARY KEY,
    agreement_id INTEGER REFERENCES license_agreements(id),
    royalty_percentage DECIMAL(5, 2) NOT NULL,
    start_date DATE,
    end_date DATE
);

CREATE TABLE agreements_history (id SERIAL PRIMARY KEY,
    agreement_id INTEGER REFERENCES license_agreements(id),
    modification_date DATE,
    description TEXT
);
