tables.table_schema:
CREATE TABLE patents (
    patent_id UUID PRIMARY KEY,
    patent_title VARCHAR(255),
    patent_description TEXT,
    patent_date DATE
);

CREATE TABLE patent_owners (
    owner_id UUID PRIMARY KEY,
    owner_name VARCHAR(255),
    owner_address TEXT
);

CREATE TABLE research_areas (
    research_area_id UUID PRIMARY KEY,
    research_area_name VARCHAR(100),
    patent_id UUID,
    FOREIGN KEY (patent_id) REFERENCES patents(patent_id)
);
