tables.table_schema:
CREATE TABLE pharmaceutical_patents (
    patent_id UUID PRIMARY KEY,
    patent_title TEXT,
    patent_holder TEXT,
    filing_date DATE
);

CREATE TABLE court_cases (
    case_id UUID PRIMARY KEY,
    case_name TEXT,
    case_type TEXT,
    court_name TEXT,
    court_date DATE
);

CREATE TABLE settlements (
    settlement_id UUID PRIMARY KEY,
    case_id UUID,
    settlement_amount DECIMAL,
    settlement_date DATE
);

CREATE TABLE patents (
    patent_id INT PRIMARY KEY,
    patent_title VARCHAR(255),
    filing_date DATE,
    litigation_status VARCHAR(50)
);

CREATE TABLE court_cases (
    case_id INT PRIMARY KEY,
    case_number VARCHAR(50),
    court_name VARCHAR(100),
    case_status VARCHAR(50),
    plaintiff VARCHAR(100),
    defendant VARCHAR(100)
);

CREATE TABLE rulings (
    ruling_id INT PRIMARY KEY,
    case_id INT REFERENCES court_cases(case_id),
    ruling_date DATE,
    ruling_description TEXT
);

CREATE TABLE settlements (
    settlement_id INT PRIMARY KEY,
    case_id INT REFERENCES court_cases(case_id),
    settlement_date DATE,
    settlement_amount DECIMAL(10, 2)
);

CREATE TABLE patent_litigation_history (
    patent_id INT PRIMARY KEY,
    lawsuit_id INT,
    court_name VARCHAR(255),
    lawsuit_details TEXT
);

CREATE TABLE settlements (
    settlement_id INT PRIMARY KEY,
    lawsuit_id INT,
    settlement_amount DECIMAL(10, 2),
    settlement_date DATE
);

CREATE TABLE lawsuit_parties (
    party_id INT PRIMARY KEY,
    lawsuit_id INT,
    party_name VARCHAR(255),
    role VARCHAR(50)
);

CREATE TABLE lawsuit_timeline (
    event_id INT PRIMARY KEY,
    lawsuit_id INT,
    event_date DATE,
    event_description TEXT
);
