tables.table_schema:
CREATE TABLE pharmaceutical_patents (
    patent_id INT PRIMARY KEY,
    patent_owner VARCHAR(255),
    transaction_type VARCHAR(50),
    transaction_date DATE
);

CREATE TABLE pharmaceutical_patents (
    patent_id UUID PRIMARY KEY,
    patent_name VARCHAR NOT NULL,
    current_owner VARCHAR NOT NULL,
    acquisition_date DATE
);

CREATE TABLE patent_acquisitions (
    acquisition_id UUID PRIMARY KEY,
    acquired_patent_id UUID REFERENCES pharmaceutical_patents(patent_id),
    acquirer_name VARCHAR NOT NULL,
    acquisition_date DATE
);

CREATE TABLE patent_transfers (
    transfer_id UUID PRIMARY KEY,
    transferred_patent_id UUID REFERENCES pharmaceutical_patents(patent_id),
    transfer_from VARCHAR NOT NULL,
    transfer_to VARCHAR NOT NULL,
    transfer_date DATE
);

CREATE TABLE pharmaceutical_patents (
  patent_id UUID PRIMARY KEY,
  patent_name VARCHAR(255),
  owner_id UUID,
  acquisition_date DATE
);

CREATE TABLE patent_owners (
  owner_id UUID PRIMARY KEY,
  owner_name VARCHAR(255),
  country VARCHAR(50)
);

CREATE TABLE patent_acquisitions (
  acquisition_id UUID PRIMARY KEY,
  patent_id UUID,
  new_owner_id UUID,
  acquisition_date DATE
);
