tables.table_schema:
CREATE TABLE patented_pharmaceutical_products (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    country_approved VARCHAR(50) NOT NULL
);

CREATE TABLE regulatory_approval (
    approval_id UUID PRIMARY KEY,
    product_id UUID REFERENCES patented_pharmaceutical_products(product_id),
    country VARCHAR(50) NOT NULL,
    approval_status VARCHAR(50) NOT NULL
);

CREATE TABLE country_details (
    country_id UUID PRIMARY KEY,
    country_name VARCHAR(100) NOT NULL,
    country_code VARCHAR(10) NOT NULL
);

CREATE TABLE products (
    product_id SERIAL PRIMARY KEY,
    product_name VARCHAR(255) NOT NULL,
    patent_number VARCHAR(50) UNIQUE,
    approval_status VARCHAR(50) NOT NULL
);

CREATE TABLE countries (
    country_id SERIAL PRIMARY KEY,
    country_name VARCHAR(100) NOT NULL,
    country_code CHAR(2) UNIQUE
);

CREATE TABLE product_country_approval (
    approval_id SERIAL PRIMARY KEY,
    product_id INT REFERENCES products(product_id),
    country_id INT REFERENCES countries(country_id),
    approval_date DATE NOT NULL
);

CREATE TABLE regulatory_agencies (
    agency_id SERIAL PRIMARY KEY,
    agency_name VARCHAR(100) NOT NULL,
    country_id INT REFERENCES countries(country_id)
);

CREATE TABLE Product (
    product_id UUID PRIMARY KEY,
    product_name VARCHAR(255),
    patent_number VARCHAR(50),
    approval_status VARCHAR(50)
);

CREATE TABLE Country (
    country_id UUID PRIMARY KEY,
    country_name VARCHAR(255),
    continent VARCHAR(50)
);

CREATE TABLE Patent (
    patent_id UUID PRIMARY KEY,
    product_id UUID REFERENCES Product(product_id),
    country_id UUID REFERENCES Country(country_id),
    expiration_date DATE
);

CREATE TABLE ApprovalStatus (
    status_id UUID PRIMARY KEY,
    status_name VARCHAR(50)
);

CREATE TABLE ProductApproval (
    product_id UUID REFERENCES Product(product_id),
    country_id UUID REFERENCES Country(country_id),
    status_id UUID REFERENCES ApprovalStatus(status_id),
    approval_date DATE
);
