tables.table_schema:
CREATE TABLE competitor_patents (
	patent_id SERIAL PRIMARY KEY,
	patent_name VARCHAR(255) NOT NULL,
	company_name VARCHAR(255) NOT NULL,
	publication_date DATE
);

CREATE TABLE pharmaceutical_strategies (
	strategy_id SERIAL PRIMARY KEY,
	strategy_name VARCHAR(255) NOT NULL,
	description TEXT,
	execution_date DATE
);

CREATE TABLE CompetitorPatents (
    patent_id SERIAL PRIMARY KEY,
    patent_name VARCHAR(255),
    competitor_name VARCHAR(255),
    publication_date DATE
);

CREATE TABLE PatentInfringements (
    infringement_id SERIAL PRIMARY KEY,
    patent_id INT REFERENCES CompetitorPatents(patent_id),
    infringement_date DATE,
    legal_action_taken VARCHAR(255)
);

CREATE TABLE MarketAnalysis (
    analysis_id SERIAL PRIMARY KEY,
    patent_id INT REFERENCES CompetitorPatents(patent_id),
    market_trend VARCHAR(255),
    analysis_date DATE
);

CREATE TABLE patents (
    patent_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    patent_title VARCHAR(255),
    publication_date DATE
);

CREATE TABLE companies (
    company_id INT PRIMARY KEY,
    company_name VARCHAR(255),
    headquarters_address VARCHAR(255),
    industry VARCHAR(100)
);

CREATE TABLE patent_owners (
    owner_id INT PRIMARY KEY,
    owner_name VARCHAR(255),
    contact_email VARCHAR(100),
    company_id INT
);

CREATE TABLE patent_usage (
    usage_id INT PRIMARY KEY,
    patent_id INT,
    usage_description TEXT,
    usage_date DATE
);
