tables.table_schema:
CREATE TABLE battery_data (
	vehicle_id INT NOT NULL,
	battery_charge DECIMAL NOT NULL,
	health_status VARCHAR(50) NOT NULL,
	last_check TIMESTAMP NOT NULL
);

CREATE TABLE BatteryLife (
    vehicle_id INT PRIMARY KEY,
    battery_percentage DECIMAL,
    estimated_range DECIMAL,
    last_charge_date TIMESTAMP
);

CREATE TABLE BatteryHealth (
    vehicle_id INT PRIMARY KEY,
    battery_health_status VARCHAR(50),
    maintenance_needed BOOLEAN,
    last_maintenance_date DATE
);

CREATE TABLE battery_info (
    vehicle_id SERIAL PRIMARY KEY,
    battery_percentage FLOAT,
    charging_status VARCHAR(10),
    last_charge_date DATE
);

CREATE TABLE battery_health (
    vehicle_id SERIAL PRIMARY KEY,
    battery_voltage FLOAT,
    battery_temperature FLOAT,
    health_status VARCHAR(10)
);

CREATE TABLE vehicle_details (
    vehicle_id SERIAL PRIMARY KEY,
    model VARCHAR(50),
    manufacturer VARCHAR(50),
    year INT
);
