tables.table_schema:
CREATE TABLE sensor_data (
    id INT,
    timestamp TIMESTAMP,
    camera_data JSONB,
    lidar_data JSONB
);

CREATE TABLE Cameras (
    camera_id UUID PRIMARY KEY,
    location VARCHAR(100),
    resolution VARCHAR(20),
    fov DECIMAL
);

CREATE TABLE LidarSensors (
    sensor_id UUID PRIMARY KEY,
    location VARCHAR(100),
    accuracy DECIMAL,
    range DECIMAL
);

CREATE TABLE cameras (
    id INT PRIMARY KEY,
    camera_id INT,
    timestamp TIMESTAMP,
    image_url TEXT
);

CREATE TABLE lidar_sensors (
    id INT PRIMARY KEY,
    sensor_id INT,
    timestamp TIMESTAMP,
    distance FLOAT
);

CREATE TABLE object_detection (
    id INT PRIMARY KEY,
    detection_id INT,
    timestamp TIMESTAMP,
    detected_object TEXT
);
