tables.table_schema:
CREATE TABLE traffic_data (
    timestamp TIMESTAMP,
    vehicle_id UUID,
    speed INT,
    congestion_level VARCHAR
);

CREATE TABLE route_optimization (
    route_id UUID,
    suggested_speed INT,
    traffic_light_status VARCHAR,
    congestion_prediction VARCHAR
);

CREATE TABLE traffic_patterns (
    id SERIAL PRIMARY KEY,
    location VARCHAR(100) NOT NULL,
    average_speed FLOAT NOT NULL,
    congestion_level VARCHAR(50) NOT NULL
);

CREATE TABLE autonomous_vehicles (
    id SERIAL PRIMARY KEY,
    vehicle_name VARCHAR(50) NOT NULL,
    current_location VARCHAR(100) NOT NULL,
    speed FLOAT NOT NULL
);

CREATE TABLE congestion_data (
    id SERIAL PRIMARY KEY,
    vehicle_id INT,
    route_id INT,
    congestion_level INT,
    timestamp TIMESTAMP
);

CREATE TABLE vehicle_info (
    vehicle_id SERIAL PRIMARY KEY,
    manufacturer TEXT,
    model TEXT,
    year INT
);

CREATE TABLE route_info (
    route_id SERIAL PRIMARY KEY,
    route_name TEXT,
    start_location TEXT,
    end_location TEXT
);

CREATE TABLE traffic_patterns (
    pattern_id SERIAL PRIMARY KEY,
    route_id INT,
    traffic_volume INT,
    congestion_level INT
);

CREATE TABLE autonomous_vehicles (
    vehicle_id SERIAL PRIMARY KEY,
    vehicle_type TEXT,
    max_speed INT
);
