tables.table_schema:
CREATE TABLE mapping_information (
    id INT PRIMARY KEY,
    location_id INT,
    map_data JSON
);

CREATE TABLE localization_data (
    id INT PRIMARY KEY,
    car_id INT,
    current_location JSON,
    destination_location JSON
);

CREATE TABLE navigation_logs (
    id INT PRIMARY KEY,
    car_id INT,
    timestamp TIMESTAMP,
    action_taken VARCHAR
);

CREATE TABLE MappingData (
    id INT PRIMARY KEY,
    latitude FLOAT NOT NULL,
    longitude FLOAT NOT NULL,
    street_name VARCHAR(255) NOT NULL
);

CREATE TABLE VehicleDetails (
    vehicle_id INT PRIMARY KEY,
    manufacturer VARCHAR(100) NOT NULL,
    model VARCHAR(100) NOT NULL,
    year INT NOT NULL
);

CREATE TABLE MappingHistory (
    id INT PRIMARY KEY,
    vehicle_id INT NOT NULL,
    mapping_timestamp TIMESTAMP NOT NULL,
    mapping_status VARCHAR(50) NOT NULL
);

CREATE TABLE MappingPoints (
    point_id SERIAL PRIMARY KEY,
    longitude FLOAT NOT NULL,
    latitude FLOAT NOT NULL,
    description TEXT
);

CREATE TABLE Distances (
    distance_id SERIAL PRIMARY KEY,
    point1_id INT NOT NULL,
    point2_id INT NOT NULL,
    distance FLOAT NOT NULL
);
