tables.table_schema:
CREATE TABLE restoration_log (
    log_id SERIAL PRIMARY KEY,
    log_date DATE NOT NULL,
    milestone TEXT NOT NULL,
    photo_url TEXT
);

CREATE TABLE restoration_photos (
    photo_id SERIAL PRIMARY KEY,
    photo_url TEXT NOT NULL,
    log_id INTEGER REFERENCES restoration_log(log_id)
);

CREATE TABLE restoration_progress_log (
    id INT PRIMARY KEY,
    date DATE,
    milestone TEXT,
    photo BYTEA
);

CREATE TABLE milestones (
    milestone_id INT PRIMARY KEY,
    milestone_name VARCHAR(255)
);

CREATE TABLE photos (
    photo_id INT PRIMARY KEY,
    photo_data BYTEA
);

CREATE TABLE progress_log (
  log_id UUID PRIMARY KEY,
  date DATE,
  milestone VARCHAR(255),
  photo BYTEA
);

CREATE TABLE restoration_dates (
  date_id UUID PRIMARY KEY,
  date DATE,
  description VARCHAR(255)
);

CREATE TABLE milestones (
  milestone_id UUID PRIMARY KEY,
  milestone VARCHAR(255),
  description TEXT
);

CREATE TABLE restoration_photos (
  photo_id UUID PRIMARY KEY,
  log_id UUID,
  photo BYTEA
);

CREATE TABLE restoration_progress (
    restoration_id INT PRIMARY KEY,
    restoration_date DATE,
    milestone TEXT,
    photo BYTEA
);
