tables.table_schema:
CREATE TABLE classic_cars (
    id INT PRIMARY KEY,
    model VARCHAR(50) NOT NULL,
    production_year INT NOT NULL,
    specifications JSONB
);

CREATE TABLE manufacturers (
    id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL
);

CREATE TABLE ClassicCarModels (
    car_id INT PRIMARY KEY,
    car_name VARCHAR(50),
    manufacturer VARCHAR(50),
    production_year INT
);

CREATE TABLE ProductionYears (
    year_id INT PRIMARY KEY,
    car_id INT REFERENCES ClassicCarModels(car_id),
    start_year INT,
    end_year INT
);

CREATE TABLE production_years (
  production_year INTEGER PRIMARY KEY,
  total_production INTEGER,
  average_price DECIMAL(10, 2),
  average_mileage INTEGER
);

CREATE TABLE car_images (
  image_id SERIAL PRIMARY KEY,
  car_id INTEGER REFERENCES classic_car_models(car_id),
  image_url TEXT
);

CREATE TABLE classic_car_models (
    id INT PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    brand VARCHAR(255) NOT NULL,
    production_year INT NOT NULL
);

CREATE TABLE production_years (
    id INT PRIMARY KEY,
    year INT NOT NULL,
    total_production INT,
    average_price DECIMAL(10, 2)
);

CREATE TABLE car_images (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    image_url VARCHAR(255) NOT NULL
);

CREATE TABLE car_reviews (
    id INT PRIMARY KEY,
    model_id INT NOT NULL,
    reviewer_name VARCHAR(255),
    review_text TEXT
);
