tables.table_schema:
CREATE TABLE Tags (
    tag_id SERIAL PRIMARY KEY,
    tag_name VARCHAR(50)
);

CREATE TABLE Repair_Manuals (
  id INT PRIMARY KEY,
  manual_name STRING,
  car_model STRING,
  publication_date DATE
);

CREATE TABLE Restoration_Guides (
  id INT PRIMARY KEY,
  guide_name STRING,
  car_make STRING,
  publication_date DATE
);

CREATE TABLE Vintage_Car_Magazines (
  id INT PRIMARY KEY,
  magazine_name STRING,
  publication_date DATE
);

CREATE TABLE RepairManuals (
    manual_id SERIAL PRIMARY KEY,
    car_model TEXT,
    publication_date DATE,
    manual_content TEXT
);

CREATE TABLE RestorationGuides (
    guide_id SERIAL PRIMARY KEY,
    car_make TEXT,
    guide_title TEXT,
    guide_content TEXT
);

CREATE TABLE CarMagazines (
    magazine_id SERIAL PRIMARY KEY,
    magazine_title TEXT,
    publisher TEXT,
    publication_date DATE
);

CREATE TABLE Users (
    user_id SERIAL PRIMARY KEY,
    username TEXT,
    email TEXT,
    password_hash TEXT
);

CREATE TABLE Favorites (
    favorite_id SERIAL PRIMARY KEY,
    user_id INT REFERENCES Users(user_id),
    document_id INT,
    document_type TEXT
);
