tables.table_schema:
CREATE TABLE warranty_info (
    part_number INT PRIMARY KEY,
    car_model VARCHAR(50),
    installation_date DATE,
    warranty_duration INT
);

CREATE TABLE part_history (
    history_id SERIAL PRIMARY KEY,
    part_number INT REFERENCES warranty_info(part_number),
    restoration_date DATE,
    restoration_notes TEXT
);

CREATE TABLE parts_warranty (
    part_id INT PRIMARY KEY,
    part_name VARCHAR(255),
    installation_date DATE,
    warranty_duration INT
);

CREATE TABLE car_models (
    model_id INT PRIMARY KEY,
    model_name VARCHAR(255),
    year_made INT,
    manufacturer VARCHAR(255)
);
