tables.table_schema:
CREATE TABLE RecallManufacturers (
    id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255),
    location VARCHAR(255),
    contact_info TEXT
);

CREATE TABLE manufacturers (
    manufacture_id INT PRIMARY KEY,
    manufacturer_name VARCHAR(255),
    headquarters_location VARCHAR(255),
    website_url VARCHAR(255)
);

CREATE TABLE vehicle_manufacturer (
  manufacturer_id SERIAL PRIMARY KEY,
  manufacturer_name VARCHAR(255) NOT NULL
);

CREATE TABLE vehicle_model (
  model_id SERIAL PRIMARY KEY,
  model_name VARCHAR(255) NOT NULL,
  manufacturer_id INT REFERENCES vehicle_manufacturer(manufacturer_id)
);

CREATE TABLE vehicle_recall (
  recall_id SERIAL PRIMARY KEY,
  recall_description TEXT NOT NULL,
  recall_date DATE NOT NULL,
  model_id INT REFERENCES vehicle_model(model_id)
);

CREATE TABLE recall_status (
  status_id SERIAL PRIMARY KEY,
  status_name VARCHAR(50) NOT NULL
);

CREATE TABLE RecallReasons (
    id SERIAL PRIMARY KEY,
    reason VARCHAR(255) NOT NULL
);

CREATE TABLE RecallStatus (
    id SERIAL PRIMARY KEY,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE RecallLocations (
    id SERIAL PRIMARY KEY,
    location VARCHAR(255) NOT NULL,
    recall_id INT REFERENCES VehicleRecalls(id)
);
