tables.table_schema:
CREATE TABLE ProjectTasks (
    task_id INT PRIMARY KEY,
    project_id INT REFERENCES CarDesignProject(project_id),
    task_description TEXT,
    task_status VARCHAR(50)
);

CREATE TABLE DesignTeam (
  team_id INT PRIMARY KEY,
  team_lead VARCHAR(100),
  team_members TEXT[],
  project_id INT REFERENCES CarDesignProject(project_id)
);

CREATE TABLE DesignIterations (
  iteration_id INT PRIMARY KEY,
  iteration_number INT,
  feedback TEXT,
  project_id INT REFERENCES CarDesignProject(project_id)
);

CREATE TABLE ProductionDetails (
  production_id INT PRIMARY KEY,
  production_stage VARCHAR(100),
  completion_date DATE,
  project_id INT REFERENCES CarDesignProject(project_id)
);

CREATE TABLE CarDesignIdeas (
    project_id SERIAL PRIMARY KEY,
    idea_description TEXT,
    created_date TIMESTAMP
);

CREATE TABLE DesignProgress (
    project_id INT REFERENCES CarDesignIdeas(project_id),
    progress_stage VARCHAR(50),
    progress_description TEXT,
    progress_date TIMESTAMP
);
