tables.table_schema:
CREATE TABLE regulatory_compliance (
  compliance_id INT PRIMARY KEY,
  car_id INT REFERENCES car_regulations(car_id),
  compliance_status VARCHAR(50),
  last_inspection DATE
);

CREATE TABLE regulatory_requirements (
  id INT PRIMARY KEY,
  requirement_name VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE car_safety (
  id INT PRIMARY KEY,
  car_id INT NOT NULL,
  safety_standard VARCHAR(100) NOT NULL
);

CREATE TABLE emissions_standards (
  id INT PRIMARY KEY,
  car_id INT NOT NULL,
  emission_limit DECIMAL(10,2) NOT NULL
);

CREATE TABLE regulatory_requirements (
  requirement_id INT PRIMARY KEY,
  requirement_name VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE car_models (
  model_id INT PRIMARY KEY,
  model_name VARCHAR(255) NOT NULL,
  manufacturer VARCHAR(255),
  year INT
);

CREATE TABLE safety_standards (
  standard_id INT PRIMARY KEY,
  standard_name VARCHAR(255) NOT NULL,
  description TEXT
);
