tables.table_schema:
CREATE TABLE traffic_data (
  timestamp TIMESTAMP,
  location VARCHAR(255),
  vehicle_count INT,
  traffic_speed INT
);

CREATE TABLE congestion_data (
  timestamp TIMESTAMP,
  location VARCHAR(255),
  congestion_level VARCHAR(50),
  road_condition VARCHAR(100)
);

CREATE TABLE TrafficStreets (
   street_id INT PRIMARY KEY,
   street_name VARCHAR(50) NOT NULL,
   congestion_level INT,
   average_speed INT
);

CREATE TABLE TrafficCameras (
   camera_id INT PRIMARY KEY,
   camera_location VARCHAR(100) NOT NULL,
   camera_status VARCHAR(20),
   street_id INT REFERENCES TrafficStreets(street_id)
);

CREATE TABLE TrafficReports (
   report_id INT PRIMARY KEY,
   report_description TEXT,
   report_time TIMESTAMP,
   camera_id INT REFERENCES TrafficCameras(camera_id)
);

CREATE TABLE TrafficUsers (
   user_id INT PRIMARY KEY,
   user_name VARCHAR(50) NOT NULL,
   user_email VARCHAR(50),
   user_role VARCHAR(20)
);

CREATE TABLE traffic_cameras (
  camera_id INT,
  location VARCHAR,
  camera_type VARCHAR,
  PRIMARY KEY (camera_id)
);
