tables.table_schema:
CREATE TABLE traffic_conditions (
  condition_id INT PRIMARY KEY,
  condition_description TEXT,
  location VARCHAR(255),
  traffic_level INT
);

CREATE TABLE real_time_traffic_information (
    id INT PRIMARY KEY,
    location VARCHAR,
    traffic_status VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE navigation_systems (
    system_id INT PRIMARY KEY,
    system_name VARCHAR,
    system_version VARCHAR
);

CREATE TABLE mobile_apps (
    app_id INT PRIMARY KEY,
    app_name VARCHAR,
    app_version VARCHAR
);

CREATE TABLE user_locations (
    user_id INT,
    user_name VARCHAR,
    location VARCHAR,
    timestamp TIMESTAMP
);

CREATE TABLE user_preferences (
    user_id INT,
    preference_key VARCHAR,
    preference_value VARCHAR
);
