tables.table_schema:
CREATE TABLE policy_info (
	id SERIAL PRIMARY KEY,
	policyholder_name VARCHAR(50),
	policyholder_age INT,
	coverage_limit DECIMAL(10, 2)
);

CREATE TABLE policyholders (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    address VARCHAR(255)
);

CREATE TABLE policy_coverage (
    policy_id INT PRIMARY KEY,
    coverage_limit DECIMAL(10,2),
    start_date DATE,
    end_date DATE
);

CREATE TABLE policyholder (
    policyholder_id INT PRIMARY KEY,
    first_name VARCHAR(50),
    last_name VARCHAR(50),
    date_of_birth DATE
);

CREATE TABLE claim (
    claim_id INT PRIMARY KEY,
    policy_id INT,
    claim_amount DECIMAL(10, 2),
    claim_date DATE
);

CREATE TABLE policyholders (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    address VARCHAR(255)
);

CREATE TABLE policies (
    policy_id INT PRIMARY KEY,
    policy_holder_id INT,
    policy_type VARCHAR(50),
    coverage_limit DECIMAL(10,2)
);

CREATE TABLE policy_types (
    type_id INT PRIMARY KEY,
    type_name VARCHAR(50),
    description TEXT
);

CREATE TABLE coverage_limits (
    limit_id INT PRIMARY KEY,
    policy_type_id INT,
    max_limit DECIMAL(10,2)
);

CREATE TABLE policyholders (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    address VARCHAR(255),
    phone_number VARCHAR(15)
);
