tables.table_schema:
CREATE TABLE claimants (
    claimant_id INT PRIMARY KEY,
    name VARCHAR(255),
    email VARCHAR(255),
    phone_number VARCHAR(20)
);

CREATE TABLE claims (
    claim_id INT PRIMARY KEY,
    claimant_id INT,
    claim_details TEXT,
    status VARCHAR(50),
    FOREIGN KEY (claimant_id) REFERENCES claimants(claimant_id)
);

CREATE TABLE claimants (
  claimant_id INT PRIMARY KEY,
  first_name VARCHAR(50),
  last_name VARCHAR(50),
  phone_number VARCHAR(15)
);

CREATE TABLE Claimants (
  claimant_id SERIAL PRIMARY KEY,
  name VARCHAR(50),
  email VARCHAR(100),
  phone_number VARCHAR(15)
);

CREATE TABLE Claimants (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    address VARCHAR(255)
);

CREATE TABLE Claims (
    claim_id INT PRIMARY KEY,
    claimant_id INT,
    claim_status VARCHAR(50),
    claim_amount DECIMAL(10, 2)
);

CREATE TABLE ClaimStatus (
    status_id INT PRIMARY KEY,
    status_name VARCHAR(50)
);

CREATE TABLE ClaimHistory (
    history_id INT PRIMARY KEY,
    claim_id INT,
    status_id INT,
    updated_at TIMESTAMP
);

CREATE TABLE ClaimAttachments (
    attachment_id INT PRIMARY KEY,
    claim_id INT,
    attachment_url TEXT
);
