tables.table_schema:
CREATE TABLE insurance_payments (
    payment_id SERIAL PRIMARY KEY,
    policy_number VARCHAR(50),
    amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE policyholders (
    policy_holder_id INT PRIMARY KEY,
    policy_holder_name VARCHAR(50),
    email_address VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE payment_records (
    record_id INT PRIMARY KEY,
    customer_id INT,
    payment_date DATE,
    amount DECIMAL(10, 2)
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    address VARCHAR(100),
    phone_number VARCHAR(15)
);

CREATE TABLE payments_history (
    history_id INT PRIMARY KEY,
    record_id INT,
    payment_date DATE,
    payment_amount DECIMAL(10, 2)
);

CREATE TABLE premiums (
    id INT PRIMARY KEY,
    policy_id INT,
    premium_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE policy_holders (
    policy_id INT PRIMARY KEY,
    holder_name VARCHAR(100),
    holder_address VARCHAR(255),
    holder_phone VARCHAR(15)
);

CREATE TABLE payments (
    payment_id INT PRIMARY KEY,
    policy_id INT,
    payment_amount DECIMAL(10, 2),
    payment_date DATE
);

CREATE TABLE policies (
    policy_id INT PRIMARY KEY,
    policy_type VARCHAR(50),
    start_date DATE,
    end_date DATE
);

CREATE TABLE insurers (
    insurer_id INT PRIMARY KEY,
    insurer_name VARCHAR(100),
    insurer_address VARCHAR(255),
    insurer_phone VARCHAR(15)
);
