tables.table_schema:
CREATE TABLE emissions_data (
    trip_id INT PRIMARY KEY,
    transportation_mode VARCHAR(50),
    distance_km DECIMAL,
    emissions_kg DECIMAL
);

CREATE TABLE modes_of_transport (
  mode_id SERIAL PRIMARY KEY,
  mode_name VARCHAR(50) NOT NULL
);

CREATE TABLE trip_emissions (
  trip_id SERIAL PRIMARY KEY,
  mode_id INT NOT NULL,
  distance_km FLOAT NOT NULL,
  carbon_emissions_kg FLOAT NOT NULL
   FOREIGN KEY (mode_id) REFERENCES modes_of_transport(mode_id)
);

CREATE TABLE transportation_modes (
    mode_id INT PRIMARY KEY,
    mode_name VARCHAR(50) NOT NULL,
    emission_factor DECIMAL(10,2) NOT NULL
);

CREATE TABLE carbon_footprint (
    trip_id INT PRIMARY KEY,
    total_emission DECIMAL(10,2) NOT NULL,
    carbon_tax DECIMAL(10,2) NOT NULL,
    FOREIGN KEY (trip_id) REFERENCES trips(trip_id)
);

CREATE TABLE transportation_modes (
    mode_id INT PRIMARY KEY,
    mode_name VARCHAR(50) NOT NULL,
    emission_factor DECIMAL(10, 2) NOT NULL
);

CREATE TABLE trips (
    trip_id INT PRIMARY KEY,
    mode_id INT REFERENCES transportation_modes(mode_id),
    distance DECIMAL(10, 2) NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE emissions (
    trip_id INT REFERENCES trips(trip_id),
    carbon_footprint DECIMAL(10, 2) NOT NULL
);

CREATE TABLE countries (
    country_id INT PRIMARY KEY,
    country_name VARCHAR(50) NOT NULL
);

CREATE TABLE transportation_modes (
    mode_id SERIAL PRIMARY KEY,
    mode_name VARCHAR(50) NOT NULL
);

CREATE TABLE trips (
    trip_id SERIAL PRIMARY KEY,
    mode_id INT REFERENCES transportation_modes(mode_id),
    distance FLOAT NOT NULL,
    date DATE NOT NULL
);

CREATE TABLE emissions (
    emission_id SERIAL PRIMARY KEY,
    trip_id INT REFERENCES trips(trip_id),
    emission_amount FLOAT NOT NULL
);

CREATE TABLE carbon_footprint (
    footprint_id SERIAL PRIMARY KEY,
    trip_id INT REFERENCES trips(trip_id),
    carbon_footprint_value FLOAT NOT NULL
);

CREATE TABLE users (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);
