tables.table_schema:
CREATE TABLE UserPreferences (
    user_id INT PRIMARY KEY,
    bike_sharing_program BOOLEAN,
    carpooling_service BOOLEAN
);

CREATE TABLE user_preferences (
    user_id INT PRIMARY KEY,
    bike_sharing_program BOOLEAN,
    carpooling_service BOOLEAN
);

CREATE TABLE user_ratings (
    user_id INT PRIMARY KEY,
    bike_sharing_rating FLOAT,
    carpooling_rating FLOAT
);

CREATE TABLE user_profiles (
    user_id INT PRIMARY KEY,
    username TEXT NOT NULL,
    email TEXT NOT NULL,
    preference TEXT
);

CREATE TABLE bike_sharing (
    bike_id INT PRIMARY KEY,
    bike_location TEXT,
    bike_status BOOLEAN
);

CREATE TABLE carpooling (
    carpool_id INT PRIMARY KEY,
    starting_location TEXT,
    destination TEXT,
    capacity INT
);

CREATE TABLE transportation_preferences (
    user_id INT,
    preferred_transport TEXT,
    FOREIGN KEY (user_id) REFERENCES user_profiles(user_id)
);
