tables.table_schema:
CREATE TABLE TrafficPatterns (
    pattern_id INT PRIMARY KEY,
    route_id INT,
    traffic_condition VARCHAR(50),
    timestamp TIMESTAMP
);

CREATE TABLE bus_routes (
	bus_route_id INT PRIMARY KEY,
	route_name VARCHAR(50),
	start_location VARCHAR(50),
	end_location VARCHAR(50)
);

CREATE TABLE electric_bus_stops (
  stop_id INT PRIMARY KEY,
  stop_name VARCHAR(50) NOT NULL,
  latitude FLOAT NOT NULL,
  longitude FLOAT NOT NULL
);

CREATE TABLE bus_routes (
  route_id INT PRIMARY KEY,
  route_name VARCHAR(50) NOT NULL,
  start_stop_id INT NOT NULL,
  end_stop_id INT NOT NULL
);

CREATE TABLE traffic_patterns (
  pattern_id INT PRIMARY KEY,
  route_id INT NOT NULL,
  time_of_day TIME NOT NULL,
  traffic_level ENUM('low', 'medium', 'high') NOT NULL
);

CREATE TABLE demand_forecast (
  forecast_id INT PRIMARY KEY,
  route_id INT NOT NULL,
  date DATE NOT NULL,
  demand INT NOT NULL
);

CREATE TABLE bus_schedule (
  schedule_id INT PRIMARY KEY,
  route_id INT NOT NULL,
  departure_time TIME NOT NULL,
  arrival_time TIME NOT NULL
);
