tables.table_schema:
CREATE TABLE driver_behavior (
    driver_id INT PRIMARY KEY,
    driving_speed INT,
    acceleration DECIMAL,
    hard_braking BOOLEAN
);

CREATE TABLE driver_behavior (
  driver_id INT,
  speeding_events INT,
  harsh_braking_events INT,
  harsh_acceleration_events INT
);

CREATE TABLE safety_metrics (
  driver_id INT,
  seatbelt_usage BOOLEAN,
  airbag_deployments INT
);

CREATE TABLE driver_data (
    driver_id INT PRIMARY KEY,
    driver_name VARCHAR(50),
    driver_age INT,
    license_plate VARCHAR(20)
);

CREATE TABLE safety_metrics (
    driver_id INT,
    safe_driving_score INT,
    accident_count INT,
    CONSTRAINT fk_driver_id FOREIGN KEY (driver_id) REFERENCES driver_data(driver_id)
);

CREATE TABLE driver_status (
  driver_id INT PRIMARY KEY,
  status VARCHAR(50),
  last_updated TIMESTAMP
);
