tables.table_schema:
CREATE TABLE inventory_turnover ( 
    product_id INT PRIMARY KEY, 
    turnover_rate FLOAT 
); 

CREATE TABLE stock_forecast ( 
    product_id INT PRIMARY KEY, 
    forecasted_stock INT 
);

CREATE TABLE inventory { 
    product_id INT PRIMARY KEY, 
    product_name VARCHAR(255), 
    quantity INT, 
    turnover_rate FLOAT 
}

CREATE TABLE Inventory { 
  product_id INT PRIMARY KEY, 
  name TEXT, 
  quantity INT, 
  turnover_rate FLOAT 
}

CREATE TABLE Sales { 
  sale_id INT PRIMARY KEY, 
  product_id INT REFERENCES Inventory(product_id), 
  sale_amount FLOAT, 
  sale_date DATE 
}

CREATE TABLE Supplier { 
  supplier_id INT PRIMARY KEY, 
  name TEXT, 
  contact_info TEXT 
}

CREATE TABLE StockForecast { 
  forecast_id INT PRIMARY KEY, 
  product_id INT REFERENCES Inventory(product_id), 
  forecasting_date DATE, 
  forecasted_quantity INT 
}

CREATE TABLE inventory_turnover_rate (
    product_id INT PRIMARY KEY,
    turnover_rate FLOAT,
    last_updated TIMESTAMP
);

CREATE TABLE stock_needs_forecast (
    forecast_id SERIAL PRIMARY KEY,
    product_id INT,
    forecast_date DATE,
    predicted_quantity INT
);

CREATE TABLE product_details (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    supplier_id INT,
    unit_price FLOAT
);

CREATE TABLE suppliers (
    supplier_id INT PRIMARY KEY,
    supplier_name VARCHAR(255),
    contact_email VARCHAR(255)
);
