CREATE TABLE vendor_expenses (
    vendor_id INT PRIMARY KEY,
    expense_type VARCHAR(50),
    expense_amount DECIMAL(10, 2),
    expense_date DATE
);

CREATE TABLE cost_savings (
    savings_id INT PRIMARY KEY,
    savings_type VARCHAR(50),
    savings_amount DECIMAL(10, 2),
    saving_date DATE
);

CREATE TABLE negotiation_outcomes (
    outcome_id INT PRIMARY KEY,
    vendor_id INT,
    negotiation_date DATE,
    negotiation_result VARCHAR(100)
);

CREATE TABLE financial_analysis (
    analysis_id INT PRIMARY KEY,
    report_date DATE,
    report_type VARCHAR(50),
    analysis_result TEXT
);

CREATE TABLE Vendor (
    vendor_id UUID PRIMARY KEY,
    vendor_name VARCHAR(255),
    vendor_location VARCHAR(255),
    vendor_type VARCHAR(50)
);

CREATE TABLE NegotiationOutcomes (
    negotiation_id UUID PRIMARY KEY,
    vendor_id UUID REFERENCES Vendor(vendor_id),
    negotiation_date DATE,
    negotiation_outcome VARCHAR(100)
);

CREATE TABLE FinancialAnalysis (
    analysis_id UUID PRIMARY KEY,
    vendor_id UUID REFERENCES Vendor(vendor_id),
    analysis_date DATE,
    analysis_results TEXT
);
