tables.table_schema:
CREATE TABLE vendor_compliance (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR(50),
    regulatory_compliance VARCHAR(50),
    certification_status VARCHAR(50)
);

CREATE TABLE regulatory_requirements (
    requirement_id INT PRIMARY KEY,
    requirement_name VARCHAR(50),
    description TEXT,
    deadline DATE
);

CREATE TABLE VendorCompliance (
    vendor_id INT,
    regulatory_requirements VARCHAR(255),
    certification_status VARCHAR(50),
    compliance_date DATE
);

CREATE TABLE RegulatoryRequirements (
    requirement_id INT,
    requirement_description TEXT,
    regulation_category VARCHAR(50)
);

CREATE TABLE Certifications (
    certification_id INT,
    certification_name VARCHAR(100),
    certification_agency VARCHAR(100),
    validity_period DATE
);

CREATE TABLE Vendor (
    vendor_id INT PRIMARY KEY,
    vendor_name VARCHAR(255) NOT NULL,
    vendor_location VARCHAR(255) NOT NULL
);

CREATE TABLE ComplianceStatus (
    status_id INT PRIMARY KEY,
    status_name VARCHAR(255) NOT NULL,
    description TEXT
);

CREATE TABLE Requirements (
    req_id INT PRIMARY KEY,
    req_name VARCHAR(255) NOT NULL,
    req_description TEXT
);

CREATE TABLE Certifications (
    cert_id INT PRIMARY KEY,
    cert_name VARCHAR(255) NOT NULL,
    cert_validity_date DATE,
    cert_description TEXT
);
