tables.table_schema:
CREATE TABLE vendor_contracts (
    contract_id INT PRIMARY KEY,
    key_terms VARCHAR(255),
    renewal_date DATE,
    performance_benchmarks TEXT
);

CREATE TABLE vendor_performance (
    vendor_id INT PRIMARY KEY,
    metric_name VARCHAR(50),
    metric_value FLOAT,
    metric_date DATE
);

CREATE TABLE contract_terms (
    term_id INT PRIMARY KEY,
    contract_id INT REFERENCES vendor_contracts(contract_id),
    term_text TEXT
);

CREATE TABLE contract_renewals (
    renewal_id INT PRIMARY KEY,
    contract_id INT REFERENCES vendor_contracts(contract_id),
    renewal_date DATE
);

CREATE TABLE benchmarks (
    benchmark_id INT PRIMARY KEY,
    contract_id INT REFERENCES vendor_contracts(contract_id),
    benchmark_text TEXT
);

CREATE TABLE vendor_contracts (
    id UUID PRIMARY KEY,
    vendor_name VARCHAR(255),
    key_terms TEXT,
    renewal_date DATE,
    performance_benchmarks JSON
);

CREATE TABLE contract_terms (
    id UUID PRIMARY KEY,
    contract_id UUID REFERENCES vendor_contracts(id),
    term_description TEXT,
    term_type VARCHAR(50),
    term_value NUMERIC
);

CREATE TABLE contract_renewals (
    id UUID PRIMARY KEY,
    contract_id UUID REFERENCES vendor_contracts(id),
    renewal_date DATE,
    renewed_by VARCHAR(100)
);

CREATE TABLE benchmarks (
    id UUID PRIMARY KEY,
    contract_id UUID REFERENCES vendor_contracts(id),
    benchmark_label VARCHAR(100),
    benchmark_value NUMERIC,
    target_value NUMERIC
);

CREATE TABLE contract_history (
    id UUID PRIMARY KEY,
    contract_id UUID REFERENCES vendor_contracts(id),
    event_date TIMESTAMP,
    event_description TEXT
);
