CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR NOT NULL,
    stock_count INT NOT NULL
);

CREATE TABLE product (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    stock_level INT
);

CREATE TABLE alert (
    id INT PRIMARY KEY,
    product_id INT,
    message VARCHAR(255),
    timestamp TIMESTAMP
);

