tables.table_schema:
CREATE TABLE StockValues (
  stock_id SERIAL PRIMARY KEY,
  stock_name VARCHAR(100) NOT NULL,
  purchase_price DECIMAL(10, 2) NOT NULL,
  current_market_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Stock (
    ID INT PRIMARY KEY,
    Name VARCHAR(255),
    PurchasePrice DECIMAL(10,2),
    MarketValue DECIMAL(10,2)
);

CREATE TABLE stock_inventory (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    purchase_price DECIMAL,
    market_value DECIMAL
);

CREATE TABLE stock_value (
    value_id INT PRIMARY KEY,
    product_id INT,
    current_market_value DECIMAL,
    FOREIGN KEY (product_id) REFERENCES stock_inventory(id)
);

CREATE TABLE stocks (
    id UUID PRIMARY KEY,
    name VARCHAR(255) NOT NULL,
    purchase_price DECIMAL(10, 2) NOT NULL,
    market_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE purchases (
    purchase_id UUID PRIMARY KEY,
    stock_id UUID REFERENCES stocks(id),
    quantity INT NOT NULL,
    purchase_date DATE NOT NULL
);

CREATE TABLE sales (
    sale_id UUID PRIMARY KEY,
    stock_id UUID REFERENCES stocks(id),
    quantity INT NOT NULL,
    sale_date DATE NOT NULL
);

CREATE TABLE stock_values (
    stock_id UUID REFERENCES stocks(id),
    date DATE NOT NULL,
    value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Stock (
    stock_id INT PRIMARY KEY,
    stock_name VARCHAR(255) NOT NULL,
    purchase_price DECIMAL(10, 2) NOT NULL,
    market_value DECIMAL(10, 2) NOT NULL
);

CREATE TABLE StockPortfolio (
    portfolio_id INT PRIMARY KEY,
    stock_id INT NOT NULL,
    quantity INT NOT NULL,
    purchase_date DATE NOT NULL
);

CREATE TABLE StockValue (
    stock_id INT PRIMARY KEY,
    current_value DECIMAL(10, 2) NOT NULL,
    valuation_date DATE NOT NULL
);
